    private val TAG = "ChattingListAdapterLog"


    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ChattingListViewHolder {
        return ChattingListViewHolder(
            BR.chattingListItem,
            parent,
            R.layout.item_chatting_list
        )

    }

    override fun onBindViewHolder(holder: ChattingListViewHolder, position: Int) {
        holder.bindItem(items[position])
        holder.initializeViews(items[position])

    }

    override fun getItemCount(): Int = items.size

    class ChattingListViewHolder(
        itemId: Int,
        parent: ViewGroup,
        layoutRes: Int
    ): BaseViewHolder<ChattingListItem, ItemChattingListBinding>(itemId, parent, layoutRes) {

        val TAG = "ChattingViewHolderLog"

        fun initializeViews(list: ChattingListItem) {
            Glide.with(itemBinding.ivThumbnail)
                .load(list.thumbnail)
                .apply(
                    RequestOptions.bitmapTransform(
                        MultiTransformation(
                            CircleCrop(),
                            RoundedCorners(DensityUtils.dpToPx(6))
                        )
                    )
                )
                .placeholder(R.color.black)
                .error(R.color.black)
                .into(itemBinding.ivThumbnail)

            itemBinding.tvNickname.text       = list.nickname
            itemBinding.tvContents.text       = list.contents
            itemBinding.lastChattingDate.text = list.date
        }

    }

    fun loadChattingList(list: List<ChattingListItem>) {
        Log.i(TAG, "list : $list")
        items.clear()
        items.addAll(list)
        notifyDataSetChanged()
    }

